// Reference: blueprint:javascript_log_in_with_replit, blueprint:javascript_database
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, boolean, index, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { relations } from "drizzle-orm";

// Session storage table (required for Replit Auth)
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table (required for Replit Auth)
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  isAdmin: boolean("is_admin").default(false).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const upsertUserSchema = createInsertSchema(users);
export type UpsertUser = z.infer<typeof upsertUserSchema>;
export type User = typeof users.$inferSelect;

// Contractor profiles
export const contractorProfiles = pgTable("contractor_profiles", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }),
  businessName: text("business_name"),
  tagline: text("tagline"),
  bio: text("bio"),
  phone: varchar("phone"),
  website: varchar("website"),

  // Legacy location field (for backward compatibility)
  location: varchar("location"),

  // Normalized location fields for better SEO
  city: varchar("city"),
  state: varchar("state"),
  zipCode: varchar("zip_code"),
  locationSlug: varchar("location_slug"),

  // Primary category for SEO (e.g., "plumber", "electrician")
  primaryCategory: varchar("primary_category"),
  categorySlug: varchar("category_slug"),

  // Secondary specialties (kept for additional services)
  specialties: text("specialties").array(),

  yearsInBusiness: varchar("years_in_business"),
  serviceAreas: text("service_areas").array(),
  bannerImageUrl: varchar("banner_image_url"),

  isClaimed: boolean("is_claimed").default(false).notNull(),
  claimStatus: varchar("claim_status").default("unclaimed").notNull(),
  verificationEmail: varchar("verification_email"),
  verificationToken: varchar("verification_token"),
  verificationTokenExpiry: timestamp("verification_token_expiry"),

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertContractorProfileSchema = createInsertSchema(contractorProfiles).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertContractorProfile = z.infer<typeof insertContractorProfileSchema>;
export type ContractorProfile = typeof contractorProfiles.$inferSelect;

// Contractor category taxonomy for SEO
export const CONTRACTOR_CATEGORIES = {
  'general-contractor': {
    name: 'General Contractors',
    slug: 'general-contractor',
    description: 'Full-service construction and renovation professionals',
    keywords: 'general contractor, construction, renovation, remodeling'
  },
  'plumber': {
    name: 'Plumbers',
    slug: 'plumber',
    description: 'Licensed plumbing professionals for residential and commercial services',
    keywords: 'plumber, plumbing, water heater, drain cleaning, pipe repair'
  },
  'electrician': {
    name: 'Electricians',
    slug: 'electrician',
    description: 'Licensed electrical contractors for installation and repair',
    keywords: 'electrician, electrical, wiring, lighting, panel upgrade'
  },
  'hvac': {
    name: 'HVAC Contractors',
    slug: 'hvac',
    description: 'Heating, ventilation, and air conditioning specialists',
    keywords: 'HVAC, heating, cooling, air conditioning, furnace, AC repair'
  },
  'roofer': {
    name: 'Roofers',
    slug: 'roofer',
    description: 'Professional roofing installation and repair services',
    keywords: 'roofer, roofing, roof repair, roof replacement, shingles'
  },
  'painter': {
    name: 'Painters',
    slug: 'painter',
    description: 'Interior and exterior painting professionals',
    keywords: 'painter, painting, interior painting, exterior painting'
  },
  'landscaper': {
    name: 'Landscapers',
    slug: 'landscaper',
    description: 'Landscape design and maintenance services',
    keywords: 'landscaper, landscaping, lawn care, yard work, garden design'
  },
  'carpenter': {
    name: 'Carpenters',
    slug: 'carpenter',
    description: 'Custom woodworking and carpentry services',
    keywords: 'carpenter, carpentry, woodworking, framing, custom cabinets'
  },
  'flooring': {
    name: 'Flooring Contractors',
    slug: 'flooring',
    description: 'Flooring installation and refinishing experts',
    keywords: 'flooring, hardwood floors, tile, carpet, laminate, floor installation'
  },
  'kitchen-remodeler': {
    name: 'Kitchen Remodelers',
    slug: 'kitchen-remodeler',
    description: 'Kitchen renovation and remodeling specialists',
    keywords: 'kitchen remodel, kitchen renovation, cabinet installation'
  },
  'bathroom-remodeler': {
    name: 'Bathroom Remodelers',
    slug: 'bathroom-remodeler',
    description: 'Bathroom renovation and remodeling experts',
    keywords: 'bathroom remodel, bathroom renovation, shower installation'
  },
  'handyman': {
    name: 'Handyman Services',
    slug: 'handyman',
    description: 'General repair and maintenance services',
    keywords: 'handyman, home repair, maintenance, odd jobs'
  }
} as const;

export type CategorySlug = keyof typeof CONTRACTOR_CATEGORIES;

// Projects
export const projects = pgTable("projects", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id, { onDelete: "cascade" }),
  title: text("title").notNull(),
  description: text("description"),
  category: varchar("category").notNull(),
  location: varchar("location"),
  isPublic: boolean("is_public").default(false).notNull(),
  completedAt: timestamp("completed_at"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertProjectSchema = createInsertSchema(projects).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertProject = z.infer<typeof insertProjectSchema>;
export type Project = typeof projects.$inferSelect;

// Project images
export const projectImages = pgTable("project_images", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id").notNull().references(() => projects.id, { onDelete: "cascade" }),
  imageUrl: text("image_url").notNull(),
  order: varchar("order").default("0"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertProjectImageSchema = createInsertSchema(projectImages).omit({
  id: true,
  createdAt: true,
});

export type InsertProjectImage = z.infer<typeof insertProjectImageSchema>;
export type ProjectImage = typeof projectImages.$inferSelect;

