  city?: string;
  category?: string;
  minRating?: number;
  minReviews?: number;
  limit?: number;
  /** Only return contractors that don't have reviews collected yet */
  needsReviews?: boolean;
  /** Only return contractors that don't have analysis yet */
  needsAnalysis?: boolean;
}

