import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { db } from "@/lib/supabaseClient";
import { supabase } from "@/integrations/supabase/client";
import { CaseStudy, CaseStudyStage, Message } from "@/types/caseStudy";
import { useToast } from "@/hooks/use-toast";

export const useCaseStudyChat = (caseStudyId: string | undefined, caseStudy?: CaseStudy) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const { toast } = useToast();

  // Fetch chat messages
  const { data: chatMessages, isLoading: isLoadingMessages, refetch: refetchMessages } = useQuery({
    queryKey: ["chatMessages", caseStudyId],
    queryFn: async () => {
      if (!caseStudyId) throw new Error("Case study ID is required");

      const { data } = await db.messages.selectWithFilter({ 
        case_study_id: caseStudyId 
      });
      return data as Message[];
    },
    enabled: !!caseStudyId
  });

  // Initialize chat with welcome message if no messages exist
  useEffect(() => {
    if (chatMessages && chatMessages.length === 0 && !isLoadingMessages) {
      initializeChat();
    } else if (chatMessages && chatMessages.length > 0) {
      setMessages(chatMessages);
    }
  }, [chatMessages, isLoadingMessages]);

  const initializeChat = async () => {
    if (!caseStudyId || !caseStudy) return;

    // Create initial welcome message based on the role
    const welcomeMessage = {
      role: "assistant" as const,
      content: getAgentIntroduction(caseStudy.current_stage),
      case_study_id: caseStudyId
    };

    try {
      const { data } = await db.messages.insert(welcomeMessage);
      if (data) {
        setMessages(data as Message[]);
      }
    } catch (error) {
      console.error("Error initializing chat:", error);
    }
  };

  const getAgentIntroduction = (stage: CaseStudyStage) => {
    switch(stage) {
      case "interview": 
        return "Welcome to the case study builder! I'm your assistant and I'll help you create a professional case study for your project. Let's start by gathering some information about your project. Could you tell me about the type of work involved and what challenges you faced?";
      case "writer_draft":
        return "I'm drafting your case study based on the information you've provided. Let me know if there's anything specific you'd like me to emphasize or include in the draft.";
      case "review":
        return "Let's review what we've put together so far. Take a look at the draft in the right panel. Does it capture your project effectively? Let me know if you'd like any changes.";
      case "finalized":
        return "Congratulations! Your case study has been finalized and is ready to be shared with potential clients. Is there anything else you'd like to know about using your case study?";
      default:
        return "Let's continue building your case study.";
    }
  };

  const handleSendMessage = async (content: string) => {
    if (!caseStudyId || !content.trim() || isProcessing) return;

    setIsProcessing(true);

    // Add user message immediately for better UX
    const tempUserMsg = {
      id: `temp-${Date.now()}`,
      role: "user" as const,
      content,
      case_study_id: caseStudyId,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, tempUserMsg]);

    try {
      // Get existing chat history for context
      const chatHistory = messages.map(msg => ({
        role: msg.role,
        content: msg.content
      }));

      // Call the Supabase Edge Function to process the message
      // UPDATED: Ensure we're sending the correct parameters expected by the function
      const { data, error } = await supabase.functions.invoke('process-case-study', {
        body: {
          caseStudyId, // Using the exact parameter name expected by the function
          userInput: content, // Using the exact parameter name expected by the function
          chatHistory // Providing chat history for context
        }
      });

      if (error) {
        console.error("Error calling process-case-study:", error);
        toast({
          title: "Error",
          description: "Failed to process your message. Please try again.",
          variant: "destructive"
        });

        // Remove temporary message on error
        setMessages(prev => prev.filter(msg => msg.id !== tempUserMsg.id));
        return;
      }

      // Refetch messages to get the real ones stored in the database
      await refetchMessages();

      // If the stage has been updated, we need to notify the parent component
      if (data?.updatedStage && caseStudy && data.updatedStage !== caseStudy.current_stage) {
        console.log("Case study stage updated:", data.updatedStage);

        // Reload the page to reflect the stage change
        // In a more sophisticated implementation, we'd use a state management solution
        // or context to update the stage without a full reload
        setTimeout(() => {
          window.location.reload();
        }, 1500);

        toast({
          title: "Stage Updated",
          description: `Moving to the ${data.updatedStage.replace('_', ' ')} stage`,
        });
      }

    } catch (error) {
      console.error("Error sending message:", error);
      toast({
        title: "Error",
        description: "Failed to send your message. Please try again.",
        variant: "destructive"
      });

      // Remove temporary message on error
      setMessages(prev => prev.filter(msg => msg.id !== tempUserMsg.id));
    } finally {
      setIsProcessing(false);
    }
  };

  return {
    messages,
    isLoadingMessages: isLoadingMessages || isProcessing,
    handleSendMessage
  };
};

