import { useState, useEffect } from "react";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { Trash2, Upload, Image as ImageIcon } from "lucide-react";
import { db } from "@/lib/supabaseClient";
import { supabase } from "@/integrations/supabase/client";
import { CaseStudyImage } from "@/types/caseStudy";

interface ImageUploaderProps {
  caseStudyId: string;
}

export const ImageUploader = ({ caseStudyId }: ImageUploaderProps) => {
  const [isUploading, setIsUploading] = useState(false);
  const [images, setImages] = useState<CaseStudyImage[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const { toast } = useToast();

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const { data, error } = await db.caseStudyImages.selectWithFilter({
          case_study_id: caseStudyId
        });

        if (error) throw error;
        if (data) {
          setImages(data as CaseStudyImage[]);
        }
      } catch (error) {
        console.error("Error fetching images:", error);
        toast({
          title: "Error",
          description: "Failed to load existing images",
          variant: "destructive",
        });
      } finally {
        setIsLoading(false);
      }
    };

    fetchImages();
  }, [caseStudyId, toast]);

  const handleUploadImage = async (file: File, type: "before" | "after" | "during") => {
    setIsUploading(true);
    try {
      // Create a unique file path
      const fileExt = file.name.split('.').pop();
      const filePath = `${caseStudyId}/${type}-${Date.now()}.${fileExt}`;

      // Upload file to Supabase Storage
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('case-study-images')
        .upload(filePath, file);

      if (uploadError) throw uploadError;

      // Get the public URL for the uploaded file
      const { data: publicURLData } = supabase.storage
        .from('case-study-images')
        .getPublicUrl(filePath);

      const imageUrl = publicURLData.publicUrl;

      // Insert the image record into the database
      const { data: imageData, error: dbError } = await db.caseStudyImages.insert({
        case_study_id: caseStudyId,
        image_url: imageUrl,
        image_type: type
      });

      if (dbError) throw dbError;

      // Call the analyze-images function to get AI description
      if (imageData && imageData.length > 0) {
        const imageId = imageData[0].id;

        toast({
          title: "Analyzing image",
          description: "This may take a moment...",
        });

        const { data: analysisData, error: analysisError } = await supabase.functions.invoke('analyze-images', {
          body: {
            imageUrl: imageUrl,
            caseStudyId: caseStudyId,
            imageId: imageId
          }
        });

        if (analysisError) {
          console.error("Error analyzing image:", analysisError);
        } else {
          console.log("Image analysis result:", analysisData);
        }

        // Update case study status
