console.log('Supabase Edge Function for Case Study Processing - Fixed parameter parsing');

serve(async (req) => {
  // Handle CORS
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    // Parse the request body
    const requestBody = await req.json();
    // Extract parameters with proper error handling and fallback
    const caseStudyId = requestBody.caseStudyId || requestBody.sessionId;
    const userInput = requestBody.userInput || requestBody.input;
    const chatHistory = requestBody.chatHistory || [];

    console.log('Processing request for case study:', caseStudyId);
    console.log('User input:', userInput);

    if (!caseStudyId) {
      return new Response(
        JSON.stringify({ error: 'Missing required parameters' }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 400,
        }
      );
    }

    // Initialize Supabase client
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    // Fetch case study details
    const { data: caseStudy, error: caseStudyError } = await supabaseClient
      .from('case_studies')
      .select('*')
      .eq('id', caseStudyId)
      .single();

    if (caseStudyError || !caseStudy) {
      return new Response(JSON.stringify({ error: 'Case study not found' }), {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 404,
      });
    }

    // Fetch case study details if they exist
    const { data: details } = await supabaseClient
      .from('case_study_details')
      .select('*')
      .eq('case_study_id', caseStudyId)
      .single();

    // Fetch case study images if they exist
    const { data: images } = await supabaseClient
      .from('case_study_images')
      .select('*')
      .eq('case_study_id', caseStudyId);

    // Convert raw chat history to properly typed ChatMessage objects
    const typedChatHistory: ChatMessage[] = Array.isArray(chatHistory)
      ? chatHistory
          .filter(
            (msg) =>
              msg &&
              typeof msg.role === 'string' &&
              typeof msg.content === 'string'
          )
          .map((msg) => ({
            role: msg.role as MessageRole,
            content: msg.content,
          }))
      : [];

    // Route to the appropriate agent based on current stage
    let agentResponse;

    const context = {
      caseStudy,
      details: details || undefined,
      images: images || [],
      chatHistory: typedChatHistory,
    };

    // Generate appropriate response based on case study stage
    if (!caseStudy.interview_complete) {
      agentResponse = await generateAgentResponse(
        'interview',
        userInput,
        context
      );
    } else if (!caseStudy.writer_draft_complete) {
      agentResponse = await generateAgentResponse(
        'writer_draft',
        userInput,
        context
      );
    } else if (!caseStudy.review_complete) {
      agentResponse = await generateAgentResponse('review', userInput, context);
    } else {
      agentResponse = await generateAgentResponse(
        'finalized',
        userInput,
        context
      );
    }

    // Store the messages in the database
    await supabaseClient.from('chat_messages').insert([
      {
        case_study_id: caseStudyId,
        role: 'user',
        content: userInput,
        timestamp: new Date().toISOString()
      },
      {
        case_study_id: caseStudyId,
        role: 'assistant',
        content: agentResponse,
        timestamp: new Date().toISOString()
      }
    ]);

    // Return response
    return new Response(
      JSON.stringify({
        response: agentResponse,
        caseStudy,
        details,
        images,
      }),
      { headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error processing request:', error);

    return new Response(JSON.stringify({ error: 'Internal server error' }), {
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      status: 500,
    });
  }
});

