// Draft generation with OpenAI
async function generateDraftWithOpenAI(caseStudy: any, details: any, images: any[], messages: any[]): Promise<any> {
  const apiKey = Deno.env.get('OPENAI_API_KEY');
  if (!apiKey) {
    throw new Error('Missing OpenAI API key');
  }

  // Extract relevant information from messages for context
  const userMessages = messages.filter(msg => msg.role === 'user').map(msg => msg.content).join("\n\n");

  // Prepare image descriptions
  const imageDescriptions = images.map(img => 
    `Image (${img.image_type}): ${img.ai_description || "No description available"}`
  ).join("\n");

  const systemPrompt = `You are a professional case study writer for contractors and home service businesses. 
  Your task is to generate a compelling case study that showcases the contractor's expertise, problem-solving abilities, 
  and the successful outcomes of their project. Create a well-structured case study with clear sections.`;

  const userPrompt = `
  Create a detailed case study for the following project:

  Project Title: ${caseStudy.title}

  Project Details:
  ${details ? `
  - Project Type: ${details.project_type || 'Not specified'}
  - Client: ${details.client_name || 'Not specified'}
  - Location: ${details.location || 'Not specified'}
  - Challenge: ${details.challenge || 'Not specified'}
  - Solution: ${details.solution || 'Not specified'}
  - Results: ${details.results || 'Not specified'}
  - Additional Notes: ${details.additional_notes || 'None'}` : 'No specific details provided.'}

  Additional context from client conversations:
  ${userMessages || 'No additional context available.'}

  Image Information:
  ${imageDescriptions || 'No images provided.'}

  Format your case study with the following sections:
  1. Introduction - Brief overview of the project and client
  2. Challenge - Detail the problems the client faced
  3. Solution - Explain how the contractor addressed these challenges
  4. Results - Describe the outcomes and benefits achieved
  5. Visual Transformation - Reference the images showing the project progress

  The case study should be professional, factual, and highlight the contractor's expertise.
  Return the content as a JSON object with a title and array of sections, each with a heading and content.`;

  const response = await fetch('https://api.openai.com/v1/chat/completions', {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${apiKey}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      model: 'gpt-4o-mini',
      messages: [
        { role: "system", content: systemPrompt },
        { role: "user", content: userPrompt }
      ],
      temperature: 0.7,
    }),
  });

  if (!response.ok) {
    const error = await response.json();
    throw new Error(`OpenAI API error: ${JSON.stringify(error)}`);
  }

  const data = await response.json();
  const content = data.choices[0].message.content;

  try {
