import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useQuery } from "@tanstack/react-query";
import { ChevronRight, Plus } from "lucide-react";
import Navbar from "@/components/Navbar";
import Footer from "@/components/Footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CaseStudySkeleton } from "@/components/case-study/CaseStudySkeleton";
import { db } from "@/lib/supabaseClient";
import { CaseStudy } from "@/types/caseStudy";

const Dashboard = () => {
  const navigate = useNavigate();
  const [isCreating, setIsCreating] = useState(false);

  const { data: caseStudies, isLoading } = useQuery({
    queryKey: ["caseStudies"],
    queryFn: async () => {
      const { data, error } = await db.caseStudies.select();

      if (error) throw error;
      return data as CaseStudy[];
    },
  });

  const createNewCaseStudy = async () => {
    setIsCreating(true);
    try {
      const { data, error } = await db.caseStudies.insert({ 
        title: "New Case Study", 
        status: "in_progress", 
        current_stage: "interview" 
      });

      if (error) throw error;
      if (data && data[0]) {
        navigate(`/case-study/${data[0].id}`);
      }
    } catch (error) {
      console.error("Error creating case study:", error);
    } finally {
      setIsCreating(false);
    }
  };

  const getCaseStudyStatusBadgeColor = (status: string) => {
    return status === "completed" 
      ? "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300" 
      : "bg-amber-100 text-amber-800 dark:bg-amber-900 dark:text-amber-300";
  };

  const getRoleDisplay = (role: string) => {
    switch(role) {
      case "interview": return "Information Gathering";
      case "writer_draft": return "Draft Creation";
      case "review": return "Review & Finalize";
      case "finalized": return "Completed";
      default: return role;
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <Navbar />

      <main className="flex-grow container max-w-7xl mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8 border-b pb-4">
          <h1 className="text-3xl font-bold text-gray-900">Your Case Studies</h1>
          <Button 
            onClick={createNewCaseStudy} 
            disabled={isCreating} 
            className="flex items-center gap-2 bg-primary hover:bg-primary/90"
          >
            <Plus size={18} />
            New Case Study
          </Button>
        </div>

        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[1, 2, 3].map((i) => (
              <CaseStudySkeleton key={i} />
            ))}
          </div>
        ) : caseStudies && caseStudies.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {caseStudies.map((study) => (
              <Card key={study.id} className="h-full flex flex-col hover:shadow-md transition-shadow border border-gray-200">
                <CardHeader className="pb-2">
                  <div className="flex justify-between items-start">
                    <CardTitle className="line-clamp-2 text-xl">{study.title}</CardTitle>
                    <Badge className={`ml-2 ${getCaseStudyStatusBadgeColor(study.status)}`}>
                      {study.status === "completed" ? "Completed" : "In Progress"}
                    </Badge>
                  </div>
                  <CardDescription className="text-sm text-gray-500">
                    Last updated: {new Date(study.updated_at).toLocaleDateString()}
                  </CardDescription>
                </CardHeader>
                <CardContent className="flex-grow">
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <span className="text-sm font-medium">Current stage:</span>
                      <span className="text-sm">{getRoleDisplay(study.current_stage)}</span>
                    </div>

                    <div className="w-full bg-gray-200 rounded-full h-1.5 mt-2">
                      <div 
                        className="bg-primary h-1.5 rounded-full" 
                        style={{ 
                          width: `${getProgressPercentage(study)}%` 
                        }}
                      ></div>
                    </div>
                  </div>
                </CardContent>
                <CardFooter className="pt-2 border-t">
                  <Button 
                    variant="outline" 
                    className="w-full flex justify-between items-center hover:bg-gray-50 border-gray-200" 
                    onClick={() => navigate(`/case-study/${study.id}`)}
                  >
                    <span>{study.status === "completed" ? "View Case Study" : "Continue Editing"}</span>
                    <ChevronRight className="h-4 w-4" />
                  </Button>
                </CardFooter>
              </Card>
            ))}
          </div>
        ) : (
          <div className="text-center py-16 bg-white rounded-lg border border-gray-200 shadow-sm">
            <h2 className="text-2xl font-medium text-gray-800 mb-4">No case studies yet</h2>
            <p className="text-gray-600 max-w-md mx-auto mb-6">
              Create your first case study to showcase your construction or renovation project in a professional format.
            </p>
            <Button 
              onClick={createNewCaseStudy} 
              disabled={isCreating}
              className="bg-primary hover:bg-primary/90"
            >
              Create Your First Case Study
            </Button>
          </div>
        )}
      </main>

      <Footer />
    </div>
  );
};

// Helper function to calculate progress percentage
const getProgressPercentage = (study: CaseStudy): number => {
  let completed = 0;
  let total = 5; // Total number of steps

  if (study.interview_details_collected) completed++;
  if (study.location_validated) completed++;
  if (study.images_uploaded) completed++;
  if (study.draft_generated) completed++;
  if (study.review_complete) completed++;

  return Math.round((completed / total) * 100);
};

export default Dashboard;

