  | 'create_post'
  | 'update_post'
  | 'delete_post'
  | 'schedule_post'
  // Analytics
  | 'get_page_insights'
  | 'get_ig_insights'
  // Ads
  | 'list_ad_accounts'
  | 'get_campaigns'
  | 'create_campaign'
  | 'update_campaign'
  | 'pause_campaign'
  | 'resume_campaign'
  | 'get_ad_insights';

/**
 * Meta API Request interface
 * @interface MetaRequest
 */
export interface MetaRequest {
  /** The action to perform */
  action: MetaAction;
  /** Optional parameters specific to each action */
  params?: MetaRequestParams;
}

/**
 * Parameters for Meta API requests
 */
export interface MetaRequestParams {
  /** Page ID (format: 123456789) */
  page_id?: string;
