 * @param bytes - File size in bytes
 * @returns Formatted string (e.g., "1.2 MB", "256 KB")
 */
const formatFileSize = (bytes: number | undefined): string => {
  if (!bytes) return ''

  const units = ['B', 'KB', 'MB', 'GB']
  let size = bytes
  let unitIndex = 0

  while (size >= 1024 && unitIndex < units.length - 1) {
    size /= 1024
    unitIndex++
