    (event: KeyboardEvent<HTMLButtonElement>, index: number) => {
      if (items.length <= 1) return

      if (event.key === 'ArrowRight' || event.key === 'ArrowLeft') {
        event.preventDefault()

        const nextIndex =
          event.key === 'ArrowRight'
            ? (index + 1) % items.length
